# edit --------------------------------------------------------------------

res <- res.orig %>% 
  dplyr::select(junc.id, enst.model, aa.seq.alt) %>% 
  distinct(aa.seq.alt, .keep_all = T) %>% 
  left_join(res.0 %>% dplyr::select(name, junc.id), by = "junc.id")

res.2 <- res %>% 
  mutate(new.name = paste0(">", name, "_", enst.model, "_alt_prot")) %>% 
  dplyr::select(new.name, aa.seq.alt) %>% 
  gather("tmp", "main", 1:2) %>% 
  mutate( no = c(seq(1, 33, 2), seq(2, 34, 2)) ) %>% 
  arrange(no) %>% 
  dplyr::select(-no)
  

